import { useDispatch, useSelector } from "react-redux";
// import { removecart } from "../redux/cartsitem";
import { useState, useRef, useEffect } from "react";

function Cart() {
  const quantity = useRef();
  const [total, setTotal] = useState([]);
  const [total2, setTotal2] = useState([]);

  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cartitem.cart);

  function handelCalc(t) {
    let data = total2.include(undefined) && t.reduce((a, b) => a + b, 0);

    setTotal(data);
    console.log(data);
  }

  useEffect(() => {
    const sumValues = (obj) => Object.values(obj).reduce((a, b) => a + b, 0);

    console.log(total2);
    console.log(sumValues(total2));
    setTotal(sumValues(total2));
  }, [total2]);

  function test() {
    console.log(cart);
  }

  return (
    <div>
      <div class="div-cart">
        <table style={{ width: "100%" }}>
          <tr>
            <th>
              Item <hr></hr>
            </th>

            <th>
              Price <hr></hr>
            </th>
            <th>
              Quantity <hr></hr>
            </th>
            <th>
              Total <hr></hr>
            </th>
          </tr>
        </table>
        <button onClick={test()}>button test</button>
      </div>
    </div>
  );
}

export default Cart;

