import { Form, Button } from "react-bootstrap";
import img1 from "../imgs/imglogin.png";

function Signup() {
  async function addtoDB(e) {
    e.preventDefault();

    let email = e.target[0].value;
    let password = e.target[1].value;
    let phoneNumber = e.target[2].value;
    let Fname = e.target[3].value;
    let Lname = e.target[4].value;

    try {
      const res = await fetch("./signup", {
        method: "POST",
        body: JSON.stringify({
          email,
          password,
          phoneNumber,
          Fname,
          Lname,
        }),
        headers: { "content-Type": "application/json" },
      });
      const data = await res.json();
      console.log(data);

      if (data.errors) {
        console.log(errors);
      }

      if (data.user) {
        location.assign("./");
      }
    } catch (err) {
      console.log(err);
    }
  }

  return (
    <div>
      <img className="login-img" src={img1} />
      <div>
        <Form
          className="login-form"
          onSubmit={(e) => {
            addtoDB(e);
          }}
        >
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email" required />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" required />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Phone Number</Form.Label>
            <Form.Control
              for="phoneNumber"
              placeholder="Enter your phone number"
              required
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>First Name</Form.Label>
            <Form.Control
              type="Fname"
              placeholder="Enter first name"
              required
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Last Name</Form.Label>
            <Form.Control type="Lname" placeholder="Enter last name" required />
          </Form.Group>

          <Button className="loginbtn" type="submit">
            Create Account
          </Button>
        </Form>
      </div>
    </div>
  );
}

export default Signup;
