import { Carousel, Button, Card } from "react-bootstrap";
import { CardGroup, Row, Col, Dropdown, ButtonGroup } from "react-bootstrap";
import { useState, useEffect } from "react";
import axios from "axios";
import Footer from "./Footer";
import { Link } from "react-router-dom";

function Shop() {
  const [product, setproduct] = useState([]);
  const [addItem, setAddItem] = useState([]);
  const [items, setItems] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3001/products").then((res) => {
      console.log(res.data);
      setproduct(res.data);
    });
    console.log(product);
  }, []);

  const priceTest = () => {};

  const genderTest = (gender) => {
    console.log(gender);
    //function for male
    // if (gender == "male") {

    // product.splice(0, product.length);
    for (let i = 0; i < product.length; i++) {
      if (product[i].gender == gender) {
        items.push(product[i]);
        setproduct(items);
        console.log(gender, " ", items);
      }

      //function for view all
      // else {
      //     setproduct(product);
      //     console.log("all items ", items);
      //   }
    }

    const addToCart = (item) => {
      // e.preventDefault();
      let items = item._id;
      let quantity = 1;
      console.log(items);
      axios
        .post("http://localhost:3001/carts/create", {
          item: items,
          quantity: quantity,
          id: "61b59f2327082b119a0c9b81",
        })
        .then((res) => {
          console.log("add saccfully" + res);
          setAddItem([...addItem, res.data]);
        })
        .catch(function (error) {
          console.log(error);
        });
      console.log(addItem);
      alert("Add Saccfully");
    };

    return (
      <div>
        <button
          onClick={() => {
            genderTest("female");
          }}
        >
          Female
        </button>
        <button
          onClick={() => {
            genderTest("male");
          }}
        >
          Male
        </button>
        <div className="container">
          <div className="shop-col1">
            <h5>FILTERS:</h5>
            <br></br>
            <h6>Gender</h6>
            <Dropdown as={ButtonGroup}>
              <Button style={{ background: "#c5269d" }}>Please select</Button>

              <Dropdown.Toggle
                split
                style={{ background: "#c5269d" }}
                id="dropdown-split-basic"
              />

              <Dropdown.Menu>
                <Dropdown.Item href="#/action-2">Select All</Dropdown.Item>
                <Dropdown.Item
                  onClick={() => {
                    genderTest("female");
                  }}
                >
                  Femail
                </Dropdown.Item>
                <Dropdown.Item
                  onClick={() => {
                    genderTest("male");
                  }}
                >
                  Male
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>

            <br></br>
            <br></br>
            <h6>Price</h6>
            <Dropdown as={ButtonGroup}>
              <Button style={{ background: "#c5269d" }}>Please select</Button>

              <Dropdown.Toggle
                split
                style={{ background: "#c5269d" }}
                id="dropdown-split-basic"
              />

              <Dropdown.Menu>
                <Dropdown.Item href="#/action-2">
                  Price (High to Low)
                </Dropdown.Item>
                <Dropdown.Item href="#/action-3">
                  Price (Low to High)
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </div>
          <div className="shop-col2">
            <div
              class="row row-cols-1 row-cols-md-3 g-4"
              style={{ margin: " 3%" }}
            >
              {product.map((item) => {
                return (
                  <div class="col">
                    <div class="card h-100">
                      <Link to={"/display/" + item._id}>
                        <img src={item.img} class="card-img-top" alt="..." />
                      </Link>
                      <div class="card-body">
                        <h6 class="card-title">{item.brand}</h6>
                        <h5 class="card-title">{item.name}</h5>
                        <small class="text-muted">Price : {item.price}</small>
                      </div>

                      <Button
                        onClick={() => {
                          addToCart(item);
                        }}
                        id="loginbtn"
                      >
                        Add To Cart
                      </Button>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  };
}
export default Shop;
