          </div>
          <div className="col2">
            <h6>Price</h6>
            <hr></hr>
          </div>

          <div className="col3">
            <h6>Quantity</h6>
            <hr></hr>
          </div>

          <div className="col4">
            <h6>Total</h6>
            <hr></hr>
          </div>
        </div>
        {
          //   cartItems.map((item) => {
          //   {
          //     console.log("item= ", item);
          //   }
          //   return (
          //     <div>
          //       <div className="container-cart">
          //         <div className="cart-col1">
          //           <img style={{ width: "10%" }} src={item.items.img} />
          //           <h6 style={{ textAlign: "center" }}>{item.items.name}</h6>
          //         </div>
          //         <div className="cart-col2">
          //           <h6>{item.items.price}</h6>
          //         </div>
          //         <div className="cart-col3">
          //           <h6>{item.quantity}</h6>
          //         </div>
          //         <div className="cart-col4">
          //           <h6>{item.subtotal}</h6>
          //         </div>
          //       </div>
          //       <hr></hr>
          //     </div>
          //   );
          // })
        }

        <div className="copon">
          <form>
            <label>
              Copon:
              <input type="text" name="copon" />
            </label>
            <input type="submit" value="Submit" />
          </form>
        </div>

        <div className="total">
          <hr></hr>
          {cartItems.map((item) => {
            <div>
              <h4>Total</h4>
              <h4>{item.total} </h4>
            </div>;
          })}
          <hr></hr>
          <h6>VTA : 15%</h6>
          <h6>- copon</h6>
          <h4>Total</h4>
        </div>
      </div>

      <Footer />
    </div>
  );
}

export default Cart;
