import axios from "axios";
import React, { useEffect, useState } from "react";
import Carousel from "react-bootstrap/carousel";
import Card from "react-bootstrap/Card";
import CardGroup from "react-bootstrap/CardGroup";
import { Row, Col, Button, Form } from "react-bootstrap";

function UpdateProduct() {
  const [product, setProduct] = useState([]);
  const [Id, setID] = useState();
  const [brand, setBrand] = useState();
  const [name, setName] = useState();
  const [description, setDescription] = useState();
  const [img, setImg] = useState();
  const [size, setSize] = useState();
  const [price, setPrice] = useState();
  const [gender, setGender] = useState();

  useEffect(() => {
    axios.get("http://localhost:3001/products").then((res) => {
      // console.log(res.data);
      setProduct(res.data);
    });
  }, []);

  function updateItem(e, id) {
    e.preventDefault();
    setID(id);
  }

  function putData(e) {
    e.preventDefault();

    axios
      .put(`http://localhost:3001/products/${Id}/update`, {
        data: {
          id: Id,
          brand: brand,
          name: name,
          description: description,
          img: img,
          size: size,
          price: price,
          gender: gender,
        },
      })

      .then((res) => {
        console.log(res);
        setProduct(res.data);
      });
  }

  return (
    <div className="AdminAddItem">
      <Form
        onSubmit={(e) => {
          putData(e);
        }}
      >
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Flower ID: </Form.Label>
          <Form.Control
            onChange={(e) => {
              setSaveID(e.target.value);
            }}
            type="text"
            placeholder="Enter ID of the Flower"
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Name of Flower</Form.Label>
          <Form.Control
            onChange={(e) => {
              setName(e.target.value);
            }}
            type="text"
            placeholder="Enter Name of the Flower"
          />
        </Form.Group>

        <Form.Group controlId="formFileSm" className="mb-3">
          <Form.Label>Flower Img</Form.Label>
          <Form.Control type="file" size="sm" />

          <Form.Control
            onChange={(e) => {
              setImg(e.target.value);
            }}
            type="text"
            placeholder="Enter Flower Img"
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Description</Form.Label>
          <Form.Control
            onChange={(e) => {
              setDescription(e.target.value);
            }}
            type="text"
            placeholder="Enter Flower Description"
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Price</Form.Label>
          <Form.Control
            type="number"
            placeholder="Enter Flower Price"
            onChange={(e) => {
              setPrice(e.target.value);
            }}
          />
        </Form.Group>

        <Button className="logbutton" variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </div>
  );
}

export default UpdateProduct;
